import { useState } from 'react';
import './App.css';
import Button from "react-bootstrap/Button"
import "bootstrap/dist/css/bootstrap.min.css"
import Modal from 'react-bootstrap/Modal';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image';


function Example() {
  const [show, setShow] = useState(false)
  const [showChild, setShowChild] = useState(false)

  const handleClose = () => {
    setShow(false)
    setShowChild(true)
  };
  const handleShow = () => setShow(true)
  const handleCloseChild = () => setShowChild(false)
  const handleShowChild = () => setShowChild(true)

  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        PURCHASE FUNDS
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Body>


          <Container>
            <Row>
              <Col>
              <Image src="imgs/left.png" thumbnail />
              </Col>
              <Col>
              <Image src="imgs/left.png/171x180" thumbnail />
              </Col>
            </Row>
          </Container>




          Woohoo, you are reading this text in a modal!
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>

        </Modal.Body>
      </Modal>

      <Modal show={showChild} onHide={handleCloseChild}>
        <Modal.Header closeButton>
          <Modal.Title>Modal Child</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you are reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseChild}>
            Close
          </Button>
          <Button variant="primary" onClick={handleCloseChild}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default Example;
